}*/

function App() {
  const [posicion, setPosicion] = useState("");
  const [afavor, setAfavor] = useState("");
  const [encontra, setEncontra] = useState("");
  const [name, setName] = useState("");
  const [teamsList, setTeamsList] = useState([]);

  const addTEAM = () => {
    Axios.post("http://localhost:3001/createTEAM", {
      name: name,
    }).then(() => {
      console.log("equipo creado");
    }).then(() => {
      setTeamsList([
        ...teamsList,
        {
          name: name,
          posicion: posicion,
          afavor: afavor,
          encontra: encontra,
        },
      ]);
    });
  };

  const getTEAMS = () => {
    Axios.get("http://localhost:3001/showTeams").then((response) => {
      setTeamsList(response.data);
    });
  };

  return (
    <div className="App">
      <div className="information">
        <label>Name:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <button onClick={addTEAM}>Add Team</button>
      </div>
      <div className="TEAMS">
        <button onClick={getTEAMS}>Show Teams</button>

        {teamsList.map((val, key) => {
          return (
            <div className="TEAM">
              <div>
                <h3>ID: {val.idTEAM}</h3>
                <h3>Name: {val.nameTEAM}</h3>
                <h3>Posici√≥n: {val.POSICION}</h3>
                <h3>Goles a favor: {val.AFAVOR}</h3>
                <h3>Goles en Contra: {val.ENCONTRA}</h3>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );  



}

export default App;
